            ID: id,
            message: this.state.msg_str,
            date: mydate
        });
    }

    render() {
        if(this.state.lastID == -1) {
            this.getLastID();
        }
        return (
            <div>
                <textarea style={this.style} placeholder='type message....' onChange={this.doChangeMsg} value={this.state.msg_str} />
                <button onClick={this.doAction}>
                    新規投稿
                </button>
            </div>
        );
    }
}

export default Fireadd;
